def solution(office : list) -> int:
    n = len(office)
    m = len(office[0])
    ans = 0
    posw_i, posw_j = 0, 0
    for i in range(n):
        for j in range(m):
            if office[i][j] == 'W':
                posw_i = i
                posw_j = j
    for i in range(n):
        for j in range(m):
            if office[i][j] == 'E':
                ans += abs(i - posw_i)
                ans += abs(j - posw_j)
    return ans
